package com.hackerrank.practice;

import java.util.*;

public class Solution {
	static String duplication(int x) {
		String a = "";
		if (a != "") {
			while (a.length() <= 500) {
				String[] s = a.split("");
				String temp = "";
				int u = 0;
				for (String i : s) {
					try {
						u = 1 - Integer.parseInt(i);
					} catch (NumberFormatException e) {
						throw new IllegalArgumentException("Not a number: " + i, e);
					}
					temp += Math.abs(u);

				}
				a = a + temp;
			}
			int i = 0;
			int[] iarray = new int[a.length()];
			for (String s : a.split("")) {
				try {
					iarray[i] = Integer.parseInt(s);
					i++;
				} catch (NumberFormatException e) {
					throw new IllegalArgumentException("Not a number: " + s, e);
				}
			}
			return String.valueOf(iarray[x]);
		}
		return null;
	}

	static String findIndex(int x) {
		String str = "";
		return String.valueOf(str.charAt(x));
	}

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int q = in.nextInt();
		for (int a0 = 0; a0 < q; a0++) {
			int x = in.nextInt();
			String result = duplication(x);
			System.out.println(result);
		}
	}
}
